mapper.update:
update sys_users
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  salt = #{salt,jdbcType=VARCHAR},
  locked = #{locked,jdbcType=BIT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, username, password, salt, locked
from sys_users
where id = #{id,jdbcType=BIGINT}
